mapper.insert:
delete from cart WHERE userid=#{userid} AND productid in
#{item.productid} ;
insert INTO orders ( orderid, userid, orderdate, status, amount ) VALUES ( #{order.orderid}, #{order.userid}, #{order.orderdate}, #{order.status}, #{order.amount} );
insert INTO orderdetails (orderid,productid,quantity,unitcost) VALUES
(#{order.orderid}, #{item.productid}, #{item.quantity}, ${item.listprice * item.quantity})
